import { createStore } from 'vuex'
import axios from 'axios'; //Axios：他のサーバーとやりとりするためのツール

export default createStore({
  state: {
  },
  getters: {
  },
  mutations: {
  },
  actions: {
  },
  getters: {
    filteredPokemons: (state) => { //stateを引数として、以下の処理をfilteredPokemonsとする
      let filtered = state.pokemons.filter(pokemon =>
        pokemon.name.toLowerCase(),includes(state.searchQuery.toLowerCase())
      );

    }
  }
})
